from flask_wtf import Formfrom wtforms import IntegerFieldfrom wtforms import DateFieldfrom wtforms import DecimalFieldfrom wtforms import TextFieldfrom wtforms import HiddenFieldfrom wtforms import PasswordFieldfrom wtforms import SelectFieldfrom wtforms.validators import DataRequiredHIRE_CHOICES = [('1', '1 hr'), ('2', '4 hrs'), ('3', '1 day'), ('4', '1 week')]PRIORITY_CHOICES = [('1', 'low'), ('2', 'high')]RATING_CHOICES = [('1', '1 star'), ('2', '2 stars'), ('3', '3 stars'), ('4', '4 stars'), ('5', '5 stars')]LOCATION_CHOICES = [('1', 'Trinity Centre'), ('2', 'Train Station'), ('3', 'Merrion Centre'), ('4', 'LGI'), ('5', 'The Edge')]class RegistrationForm(Form):	id = HiddenField()	email = TextField('email', validators = [DataRequired()])	firstName = TextField('firstName', validators = [DataRequired()])	lastName = TextField('lastName', validators = [DataRequired()])	password = PasswordField('password', validators = [DataRequired()])	repeatPassword = PasswordField('repeatPassword', validators = [DataRequired()])class LogIn(Form):	id = TextField('id')	email = TextField('email', validators = [DataRequired()])	password = PasswordField('password', validators = [DataRequired()])class ChangePassword(Form):	oldPassword = PasswordField('oldPassword', validators = [DataRequired()])	newPassword = PasswordField('newPassword', validators = [DataRequired()])	repeatNew = PasswordField('repeatNew', validators = [DataRequired()])class StoreCardDetails(Form):    id = TextField('id')    firstName = TextField('firstName', validators = [DataRequired()])    lastName = TextField('lastName', validators = [DataRequired()])    accountNo = IntegerField('accountno', validators = [DataRequired()])    sortCode = IntegerField('sortCode', validators = [DataRequired()])    expiry = DateField('expiry', validators = [DataRequired()])    CVC = IntegerField('CVC', validators = [DataRequired()])    class CreateBooking(Form):    id = TextField('id')    scooterid = TextField('scooterid')    hirePeriod = SelectField(u'period', choices = HIRE_CHOICES, validators = [DataRequired()])    pickupLoc = SelectField(u'pickup', choices = LOCATION_CHOICES, validators = [DataRequired()])    dropOffLoc = SelectField(u'dropoff', choices = LOCATION_CHOICES)    class Feedback(Form):    id = TextField('id')    rating = SelectField(u'rating', choices = RATING_CHOICES, validators = [DataRequired()])    message = TextField('message', validators = [DataRequired()])    priority = SelectField('priority', choices = PRIORITY_CHOICES, validators = [DataRequired()])